from etcd3 import AioClient

from compel_shared.conf.mongo_mem_cache import MongoMemCache
from compel_shared.params.recog_template import RecogTemplate


class RecogTemplateConfig(MongoMemCache):

    def __init__(self, db, etcd_client: AioClient, provider='default'):
        super().__init__(db, etcd_client, 'recog_template', RecogTemplate, provider=provider, key='_id')
