import typing
from dataclasses import field

from marshmallow_dataclass import dataclass

from compel_shared.conf import ConfigElement
from compel_shared.schema import OrderedSchema


@dataclass(base_schema=OrderedSchema)
class TechConfig(ConfigElement):
    m10: typing.List[str] = field(metadata=dict(
        title='m10',
        description='Технологический тип для срочного производства модулей',
    ))
    ems: typing.List[str] = field(metadata=dict(
        title='ems',
        description='Технологический тип для контрактного производства',
    ))
